INSERT INTO `wordpress`.`wp_wc_admin_notes` VALUES (68, 'wc-admin-new-sales-record', 'info', 'en_US', 'New sales record!', 'Woohoo, April 23rd was your record day for sales! Net sales was KSh\xa08,400.00 beating the previous record of KSh\xa02,400.00 set on April 21st.', '{"old_record_date":"2024-04-21","old_record_amt":2400,"new_record_date":"2024-04-23","new_record_amt":8400}', 'unactioned', 'woocommerce-admin', '2024-4-24 3:38:4', NULL, 0, 'plain', '', 0, 0, 'info');
INSERT INTO `wordpress`.`wp_wc_admin_notes` VALUES (69, 'wc-admin-real-time-order-alerts', 'info', 'en_US', 'Get real-time order alerts anywhere', 'Get notifications about store activity, including new orders and product reviews directly on your mobile devices with the Woo app.', '{}', 'unactioned', 'woocommerce-admin', '2024-7-7 7:25:56', NULL, 0, 'plain', '', 0, 0, 'info');
